import React, { useState, useEffect, useRef } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import styled from 'styled-components';
import { toggleChat, setActiveTab, addMessage } from '../store/chatSlice';
import socketService from '../services/socketService';

const ChatOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.5);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 2000;
`;

const ChatContainer = styled.div`
  background: white;
  border-radius: 12px;
  width: 90%;
  max-width: 500px;
  max-height: 80vh;
  display: flex;
  flex-direction: column;
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.2);
`;

const ChatHeader = styled.div`
  padding: 20px;
  border-bottom: 1px solid #F2F2F2;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const ChatTitle = styled.h3`
  font-size: 18px;
  font-weight: 600;
  color: #373737;
`;

const CloseButton = styled.button`
  background: none;
  border: none;
  font-size: 24px;
  color: #6E6E6E;
  cursor: pointer;
  padding: 0;
  width: 32px;
  height: 32px;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 50%;
  transition: background-color 0.2s ease;
  
  &:hover {
    background: #F2F2F2;
  }
`;

const TabContainer = styled.div`
  display: flex;
  border-bottom: 1px solid #F2F2F2;
`;

const Tab = styled.button`
  flex: 1;
  padding: 16px;
  border: none;
  background: ${props => props.isActive ? '#7765DA' : 'transparent'};
  color: ${props => props.isActive ? 'white' : '#6E6E6E'};
  font-size: 14px;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.2s ease;
  
  &:hover {
    background: ${props => props.isActive ? '#6B5BC7' : '#F8F7FF'};
  }
`;

const ChatContent = styled.div`
  flex: 1;
  padding: 20px;
  overflow-y: auto;
  max-height: 400px;
`;

const MessagesContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 16px;
`;

const MessageItem = styled.div`
  display: flex;
  flex-direction: column;
  align-items: ${props => props.isOwn ? 'flex-end' : 'flex-start'};
`;

const MessageBubble = styled.div`
  max-width: 80%;
  padding: 12px 16px;
  border-radius: 16px;
  background: ${props => props.isOwn ? '#7765DA' : '#F2F2F2'};
  color: ${props => props.isOwn ? 'white' : '#373737'};
  font-size: 14px;
  line-height: 1.4;
  word-wrap: break-word;
`;

const MessageSender = styled.div`
  font-size: 12px;
  color: #6E6E6E;
  margin-bottom: 4px;
  font-weight: 500;
`;

const ParticipantsList = styled.div`
  display: flex;
  flex-direction: column;
  gap: 12px;
`;

const ParticipantItem = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 12px;
  background: #F8F9FA;
  border-radius: 8px;
  border: 1px solid #E9ECEF;
`;

const ParticipantName = styled.div`
  font-size: 14px;
  color: #373737;
  font-weight: 500;
`;

const ParticipantStatus = styled.div`
  font-size: 12px;
  color: #6E6E6E;
`;

const ChatInput = styled.div`
  padding: 20px;
  border-top: 1px solid #F2F2F2;
  display: flex;
  gap: 12px;
`;

const MessageInput = styled.input`
  flex: 1;
  padding: 12px 16px;
  border: 2px solid #F2F2F2;
  border-radius: 8px;
  font-size: 14px;
  outline: none;
  transition: border-color 0.2s ease;
  
  &:focus {
    border-color: #7765DA;
  }
  
  &::placeholder {
    color: #6E6E6E;
  }
`;

const SendButton = styled.button`
  background: #7765DA;
  color: white;
  border: none;
  border-radius: 8px;
  padding: 12px 20px;
  font-size: 14px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.2s ease;
  
  &:hover {
    background: #6B5BC7;
  }
  
  &:disabled {
    opacity: 0.5;
    cursor: not-allowed;
  }
`;

const ChatPopup = () => {
  const [message, setMessage] = useState('');
  const [currentUser] = useState('User ' + Math.floor(Math.random() * 1000) + 1);
  const dispatch = useDispatch();
  const { isOpen, activeTab, messages, participants } = useSelector((state) => state.chat);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Listen for real-time chat events
  useEffect(() => {
    if (isOpen) {
      // Listen for new messages
      socketService.onMessageReceived((messageData) => {
        dispatch(addMessage({
          id: Date.now().toString(),
          sender: messageData.sender,
          text: messageData.text,
          isOwn: false
        }));
      });

      // Listen for typing indicators
      socketService.onUserTyping((userData) => {
        // You can implement typing indicators here
        console.log('User typing:', userData);
      });

      socketService.onUserStoppedTyping((userData) => {
        // You can implement typing indicators here
        console.log('User stopped typing:', userData);
      });

      // Cleanup event listeners
      return () => {
        socketService.off('message_received');
        socketService.off('user_typing');
        socketService.off('user_stopped_typing');
      };
    }
  }, [isOpen, dispatch]);

  if (!isOpen) return null;

  const handleClose = () => {
    dispatch(toggleChat());
  };

  const handleTabChange = (tab) => {
    dispatch(setActiveTab(tab));
  };

  const handleSendMessage = () => {
    if (message.trim()) {
      dispatch(addMessage({
        sender: currentUser,
        text: message.trim(),
        isOwn: true
      }));
      setMessage('');
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSendMessage();
    }
  };

  return (
    <ChatOverlay onClick={handleClose}>
      <ChatContainer onClick={(e) => e.stopPropagation()}>
        <ChatHeader>
          <ChatTitle>Chat & Participants</ChatTitle>
          <CloseButton onClick={handleClose}>&times;</CloseButton>
        </ChatHeader>

        <TabContainer>
          <Tab
            isActive={activeTab === 'chat'}
            onClick={() => handleTabChange('chat')}
          >
            Chat
          </Tab>
          <Tab
            isActive={activeTab === 'participants'}
            onClick={() => handleTabChange('participants')}
          >
            Participants
          </Tab>
        </TabContainer>

        <ChatContent>
          {activeTab === 'chat' ? (
            <MessagesContainer>
              {messages.map((msg) => (
                <MessageItem key={msg.id} isOwn={msg.isOwn}>
                  <MessageSender>{msg.sender}</MessageSender>
                  <MessageBubble isOwn={msg.isOwn}>
                    {msg.text}
                  </MessageBubble>
                </MessageItem>
              ))}
              <div ref={messagesEndRef} />
            </MessagesContainer>
          ) : (
            <ParticipantsList>
              {participants.length > 0 ? (
                participants.map((participant) => (
                  <ParticipantItem key={participant.id}>
                    <ParticipantName>{participant.name}</ParticipantName>
                    <ParticipantStatus>Online</ParticipantStatus>
                  </ParticipantItem>
                ))
              ) : (
                <div style={{ textAlign: 'center', color: '#6E6E6E', padding: '20px' }}>
                  No participants yet
                </div>
              )}
            </ParticipantsList>
          )}
        </ChatContent>

        {activeTab === 'chat' && (
          <ChatInput>
            <MessageInput
              type="text"
              placeholder="Type your message..."
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              onKeyPress={handleKeyPress}
            />
            <SendButton onClick={handleSendMessage} disabled={!message.trim()}>
              Send
            </SendButton>
          </ChatInput>
        )}
      </ChatContainer>
    </ChatOverlay>
  );
};

export default ChatPopup;
