import { io } from 'socket.io-client';

class SocketService {
  constructor() {
    this.socket = null;
    this.isConnected = false;
  }

  connect(userId, role) {
    if (this.socket && this.isConnected) {
      return this.socket;
    }

    // Connect to your backend server
    this.socket = io('http://localhost:5000', {
      transports: ['websocket', 'polling'],
      autoConnect: true,
    });

    this.socket.on('connect', () => {
      console.log('Connected to server');
      this.isConnected = true;
      
      // Authenticate the user with the server
      this.socket.emit('authenticate', { userId, role });
    });

    this.socket.on('disconnect', () => {
      console.log('Disconnected from server');
      this.isConnected = false;
    });

    this.socket.on('connect_error', (error) => {
      console.error('Connection error:', error);
      this.isConnected = false;
    });

    return this.socket;
  }

  disconnect() {
    if (this.socket) {
      this.socket.disconnect();
      this.socket = null;
      this.isConnected = false;
    }
  }

  // Poll-related events
  onPollCreated(callback) {
    if (this.socket) {
      this.socket.on('poll_created', callback);
    }
  }

  onPollEnded(callback) {
    if (this.socket) {
      this.socket.on('poll_ended', callback);
    }
  }

  onAnswerSubmitted(callback) {
    if (this.socket) {
      this.socket.on('answer_submitted', callback);
    }
  }

  // Chat-related events
  onMessageReceived(callback) {
    if (this.socket) {
      this.socket.on('message_received', callback);
    }
  }

  onUserTyping(callback) {
    if (this.socket) {
      this.socket.on('user_typing', callback);
    }
  }

  onUserStoppedTyping(callback) {
    if (this.socket) {
      this.socket.on('user_stopped_typing', callback);
    }
  }

  // User status events
  onUserJoined(callback) {
    if (this.socket) {
      this.socket.on('user_joined', callback);
    }
  }

  onUserLeft(callback) {
    if (this.socket) {
      this.socket.on('user_left', callback);
    }
  }

  // Emit events
  createPoll(pollData) {
    if (this.socket) {
      this.socket.emit('create_poll', pollData);
    }
  }

  submitAnswer(answerData) {
    if (this.socket) {
      this.socket.emit('submit_answer', answerData);
    }
  }

  endPoll(pollId) {
    if (this.socket) {
      this.socket.emit('end_poll', { pollId });
    }
  }

  sendMessage(messageData) {
    if (this.socket) {
      this.socket.emit('send_message', messageData);
    }
  }

  startTyping() {
    if (this.socket) {
      this.socket.emit('typing_start');
    }
  }

  stopTyping() {
    if (this.socket) {
      this.socket.emit('typing_stop');
    }
  }

  // Remove event listeners
  off(event) {
    if (this.socket) {
      this.socket.off(event);
    }
  }

  // Get connection status
  getConnectionStatus() {
    return this.isConnected;
  }
}

// Export a singleton instance
export default new SocketService();
